import React, { useState } from 'react';
import "./App.css"

function App() {
  const [itemList, setItemList] = useState([]);
  const [currentInput, setCurrentInput] = useState('');

  const handleInputChange = (event) => {
    setCurrentInput(event.target.value);
  };

  const handleKeyPress = (event) => {
    if (event.key === 'Enter') {
      setItemList([...itemList, currentInput]);
      setCurrentInput('');
    }
  };

  const handleDeleteToDo = (event) => {
    const currentInput  = [...itemList];
    currentInput.splice(event,1);
    setItemList(currentInput);

  }


  return (
    <div>

    <img
  src={process.env.PUBLIC_URL + '/m1.png'}
  alt="Description"
  style={{ width: '100%', maxHeight: '300px' }}
/> 
 <h1 style={{ position: 'absolute', color :' #fff' ,top: 80, left: 50, textAlign: 'center', padding: '10px', zIndex: 2 }}>
  TO DO
</h1>
<input  placeholder='Create a new todo'
        type="text"
        value={currentInput}
        onChange={handleInputChange}
        onKeyPress={handleKeyPress}
        style={{ border: '1px solid #fff',
          boxSizing: 'border-box',
          zIndex: '1',
          margin: '5px 300px',
          top: '-90px',
          width: '650px',
          padding: '20px',
          position: 'relative', 

        }}
      />

       <div className="App"> 
      <ul>
        {itemList.map((item, index) => (
          <li key={index}><input type='checkbox' /> {item} <button onClick={() => handleDeleteToDo(index)}>Delete</button></li>
        ))}
       </ul>
    </div>


    </div>
  );
}

export default App;
