import React, { useState } from 'react';
import "./App.css";

function App() {
  const [itemList, setItemList] = useState([]);
  const [currentInput, setCurrentInput] = useState('');

  const handleInputChange = (event) => {
    setCurrentInput(event.target.value);
  };

  const handleKeyPress = (event) => {
    if (event.key === 'Enter') {
      setItemList([...itemList, { text: currentInput, checked: false }]);
      setCurrentInput('');
    }
  };

  const handleDeleteToDo = (index) => {
    const updatedList = [...itemList];
    updatedList.splice(index, 1);
    setItemList(updatedList);
  }
  const handleToggleToDo = (index) => {
    const currentInput  = [...itemList];
    currentInput[index].checked = !currentInput[index].checked;
    setItemList(currentInput);




  }

  return (
    <div>
      <img
        src={process.env.PUBLIC_URL + '/m1.png'}
        alt="Description"
        style={{ width: '100%', maxHeight: '300px' }}
      />
      <h1 style={{ position: 'absolute', color: '#fff', top: 80, left: 50, textAlign: 'center', padding: '10px', zIndex: 2 }}>
        TO DO
      </h1>
      <input
        placeholder='Create a new todo'
        type="text"
        value={currentInput}
        onChange={handleInputChange}
        onKeyPress={handleKeyPress}
        style={{
          border: '1px solid #fff',
          boxSizing: 'border-box',
          zIndex: '1',
          margin: '5px 300px',
          top: '-90px',
          width: '650px',
          padding: '20px',
          position: 'relative',
        }}
      />

      <div className="App">
        <ul>
          {itemList.map((item, index) => (
            <li >
              <input type='checkbox' checked={item.checked} onChange={() => handleToggleToDo(index) } /> {item.text}
              <button className="button_delete" onClick={() => handleDeleteToDo(index)}>Delete</button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default App;
