import React, { useState } from 'react';

function TodoItem({ todo, index, onDelete, onToggle }) {
  return (
    <div className="todo-item">
      <span
        // style={{
        //   textDecoration: todo.done ? 'line-through' : 'none',
        //   marginRight: '10px',
        // }}
        onClick={() => onToggle(index)}
      >
        {todo.text}
      </span>
      <div
        // style={{
        //   backgroundColor: todo.done ? 'rgba(0, 255, 0, 0.3)' : 'rgba(255, 0, 0, 0.3)',
        //   padding: '5px',
        //   borderRadius: '5px',
        //   visibility: todo.done ? 'visible' : 'hidden',
        // }}
      >
        {todo.done ? 'Done' : 'Not Done'}
      </div>
      <span
        // style={{
        //   marginLeft: 'auto',
        //   visibility: 'hidden',
        // }}
        className="delete-span"
        onClick={() => onDelete(index)}
      >
        ‚ùå
      </span>
      <div
        style={{
          position: 'absolute',
          left: '0',
          width: '100%',
          height: '100%',
        }}
        onMouseEnter={(e) => {
          const spanElement = e.currentTarget.querySelector('.delete-span');
          if (spanElement) {
            spanElement.style.visibility = 'visible';
          }
        }}
        onMouseLeave={(e) => {
          const spanElement = e.currentTarget.querySelector('.delete-span');
          if (spanElement) {
            spanElement.style.visibility = 'hidden';
          }
        }}
      ></div>
    </div>
  );
}

function App() {
  const [itemList, setItemList] = useState([]);
  const [currentInput, setCurrentInput] = useState('');

  const handleInputChange = (event) => {
    setCurrentInput(event.target.value);
  };

  const handleKeyPress = (event) => {
    if (event.key === 'Enter') {
      setItemList([...itemList, currentInput]);
      setCurrentInput('');
    }
  };
  const handleToggle = (index) => {
    const updatedList = [...itemList];
    updatedList[index].done = !updatedList[index].done;
    setItemList(updatedList);
  };

  const handleDelete = (index) => {
    const updatedList = [...itemList];
    updatedList.splice(index, 1);
    setItemList(updatedList);
  };


  return (
    <div>
      <input
        type="text"
        value={currentInput}
        onChange={handleInputChange}
        onKeyPress={handleKeyPress}
      />
      <ul>
      {itemList.map((todo, index) => (
          <li key={index}>
            <TodoItem todo={todo} index={index} onDelete={handleDelete} onToggle={handleToggle} />
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
